import { useEffect } from 'react';
import { useFetcher } from 'react-router-dom';

import classes from './NewsletterSignup.module.css';

function NewsletterSignup() {
  // 使用 useFetcher() 获取路由的数据获取器
  const fetcher = useFetcher();
  // 从数据获取器中获取数据和状态
  const { data, state } = fetcher;

  useEffect(() => {
    // 当状态为 'idle' 且数据存在且包含消息时，显示警告消息
    if (state === 'idle' && data && data.message) {
      window.alert(data.message);
    }
  }, [data, state]);

  return (
    // 使用数据获取器的 Form 组件来提交表单
    <fetcher.Form
      method="post" // 表单提交方法
      action="/newsletter" // 表单提交的目标地址
      className={classes.newsletter} // 表单的样式类名
    >
      {/* 输入电子邮件地址的输入框 */}
      <input
        type="email"
        placeholder="Sign up for newsletter..." // 输入框的占位符
        aria-label="Sign up for newsletter" // 输入框的无障碍标签
      />
      {/* 提交按钮 */}
      <button>Sign up</button>
    </fetcher.Form>
  );
}

export default NewsletterSignup;
