import { redirect } from 'react-router-dom';

// 定义一个 action 函数
export function action() {
  // 从本地存储中移除令牌和过期时间信息
  localStorage.removeItem('token');
  localStorage.removeItem('expiration');
  
  // 重定向到根路径 '/'
  return redirect('/');
}
